import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-nouvel-article',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './nouvel-article.component.html',
  styleUrls: ['./nouvel-article.component.scss']
})
export class NouvelArticleComponent {

}
<div class="col">
    <div class="col-md-12">
        <img src="assets/product.png" class="rounded-circle" width="200px" height="200px" />
    </div>
    <div class="col-md-12">
        <h2>
            <i class="fas fa-info-circle blue-color"></i>&nbsp;
            Information de l'article
        </h2>
    </div>
</div>
